import React from 'react';
import Cartas from '../Cartas';

const Mano = ({ cartas, titulo, mostrarPrimeraCarta }) => {
  const calcularPuntuacion = () => {
    let puntuacion = 0;
    let ases = 0;

    for (const carta of cartas) {
      if (carta.numero === 'A') {
        ases++;
        puntuacion += 11;
      } else if (['K', 'Q', 'J'].includes(carta.numero)) {
        puntuacion += 10;
      } else {
        puntuacion += parseInt(carta.numero);
      }
    }
    while (puntuacion > 21 && ases) {
      puntuacion -= 10;
      ases--;
    }
    return puntuacion;
  };

  return (
    <div className="mano">
      <div className="cartas-container">
        {cartas.map((carta, index) => (
          <div key={index} style={{ marginLeft: `${index * 20}px` }}>
            <Cartas numero={carta.numero} palo={carta.palo} oculto={index === 0 && mostrarPrimeraCarta} />
          </div>
        ))}
      </div>
      <p>Puntuaci√≥n: {calcularPuntuacion()}</p>
    </div>
  );
};

export default Mano;
