import React, { useState } from 'react';
import Mano from '../Mano';

const Jugador = ({ cartas, solicitarCarta, plantarse, cambiarCartaCrupier }) => {

    return (
        <div className="jugador">
            <Mano cartas={cartas} titulo="Jugador" />
            <div className='jugadorBotones'>
                <div id="boton1">
                    <button onClick={solicitarCarta}>Solicitar Carta</button>
                </div>
                <div id="boton2">
                    <button onClick={handlePlantarse}>Plantarse</button>
                </div>
            </div>
        </div>
    );
};

export default Jugador;
