import React from 'react';
import Mano from '../Mano';

const Crupier = ({ cartas, solicitarCarta }) => {
    const puntuacion = calcularPuntuacion(cartas);
  
    return (
      <div className="crupier">
        <h2>Crupier</h2>
        <div className="cartas-container">
          {cartas.map((carta, index) => (
            <div key={index} style={{ marginLeft: `${index * 20}px` }}>
              <Cartas
                numero={carta.numero}
                palo={carta.palo}
                isFaceDown={carta.isFaceDown}
              />
            </div>
          ))}
        </div>
        <p>Puntuaci√≥n: {carta.isFaceDown ? '?' : puntuacion}</p>
        <button className="boton2" onClick={solicitarCarta} disabled={carta.isFaceDown}>
          Solicitar Carta
        </button>
      </div>
    );
  };
  
  export default Crupier;
