import React, { useState } from 'react';
import Mano from '../Mano';

const Crupier = ({ cartas, solicitarCarta }) => {
    const [mostrarPrimeraCarta, setMostrarPrimeraCarta] = useState(false); // Nuevo estado aquí

    const mostrarCartaBocaAbajo = cartas.length > 1 && !mostrarPrimeraCarta;
    
    const plantarse = () => {
        setMostrarPrimeraCarta(true); // Actualizamos el estado al plantarse
        solicitarCarta();
    };

    return (
        <div className="crupier">
            <Mano cartas={cartas.map((carta, index) => {
                if (index === 0 && mostrarCartaBocaAbajo) { 
                    return {...carta, isFaceDown: true};
                }
                return carta;
            })} titulo="Crupier" />
            <div className="botonCrupier">
                <button className="boton4" onClick={solicitarCarta}>
                    Solicitar Carta
                </button>
                <button className="boton4" onClick={plantarse}>
                    Plantarse
                </button>
            </div>
        </div>
    );
};

export default Crupier;
