import React from 'react';
import Mano from '../Mano';

const Jugador = ({ cartas, solicitarCarta, plantarse }) => {
    const calcularPuntuacion = () => {
        let puntuacion = 0;
        let ases = 0;

        for (const carta of cartas) {
            if (carta.numero === 'A') {
                ases++;
                puntuacion += 11;
            } else if (['K', 'Q', 'J'].includes(carta.numero)) {
                puntuacion += 10;
            } else {
                puntuacion += parseInt(carta.numero);
            }
        }

        while (puntuacion > 21 && ases) {
            puntuacion -= 10;
            ases--;
        }

        return puntuacion;
    };

    const handleSolicitarCarta = () => {
        const puntuacionActual = calcularPuntuacion();
        if (puntuacionActual < 21) {
            solicitarCarta();
        } else {
            window.alert('Te has pasado de 21 puntos. No puedes solicitar más cartas.');
        }
    };

    return (
        <div className="jugador">
            <Mano cartas={cartas} titulo="Jugador" />
            <div className='jugadorBotones'>
                <div id="boton1">
                    <button onClick={handleSolicitarCarta}>Solicitar Carta</button>
                </div>
                <div id="boton2">
                    <button onClick={plantarse}>Plantarse</button>
                </div>
            </div>
        </div>
    );
};

export default Jugador;
