import React from 'react';
import Cartas from '../Cartas';

const Mano = ({ cartas, titulo }) => {
    // Calcular la puntuación total de la mano
        const calcularPuntuacion = () => {
            let puntuacion = 0;
            let ases = 0;

            for (const carta of cartas) {
                if (carta.numero === 'A') {
                    ases++;
                    puntuacion += 11;
                } else if (['K', 'Q', 'J'].includes(carta.numero)) {
                    puntuacion += 10;
                } else {
                    puntuacion += parseInt(carta.numero);
                }
            }
            while (puntuacion > 21 && ases) {
                puntuacion -= 10;
                ases--;
            }
            return puntuacion;
        };
        return (
            <div className="mano">
                    <div className="cartas-container" style={{ display: 'flex'}}>
                        {cartas.map((carta, index) => (
                            <div key={index} style={{ marginLeft: `${index * 20}px` }}>
                            <Cartas numero={carta.numero} palo={carta.palo} />
                            </div>
                        ))}
                    </div>
                        <p>Puntuación: {calcularPuntuacion()}</p>
                    </div>
                );
            };

export default Mano;
