import React, { useState } from 'react';
import Mano from '../Mano';

const Crupier = ({ cartas, solicitarCarta  }) => {
    const [mostrarPrimeraCarta, setMostrarPrimeraCarta] = useState(false);

const mostrarCarta = (carta, index) => {
    if (index === 0 && !mostrarPrimeraCarta) {
      return (
        <div key={index} style={{ marginLeft: `${index * 20}px` }}>
          <Cartas numero="0" palo="bocaAbajo" />
        </div>
      );
    } else {
      return (
        <div key={index} style={{ marginLeft: `${index * 20}px` }}>
          <Cartas numero={carta.numero} palo={carta.palo} />
        </div>
      );
    }
  };

  return (
    <div className="crupier">
      <Mano cartas={cartas} titulo="Crupier" />
      <div className="botonCrupier">
        <button className="boton4" onClick={solicitarCarta}>
          Solicitar Carta
        </button>
      </div>
    </div>
  );
};

export default Crupier;
