import React from 'react';
import Mano from '../Mano';

const Crupier = ({ cartas, mostrarPrimeraCarta, solicitarCarta }) => {
    return (
        <div className="crupier" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <Mano cartas={cartas} titulo="Crupier" />
            {!mostrarPrimeraCarta}
            {mostrarPrimeraCarta ? <button className="boton4" onClick={solicitarCarta}>Solicitar Carta</button> : null}
        </div>
    );
};

export default Crupier;
