import React from 'react';
import Mano from '../Mano';

const Crupier = ({ cartas, mostrarPrimeraCarta, solicitarCarta }) => {

    const cambiarCartaBocaAbajo = () => {
    const nuevaCarta = { numero: '0', palo: 'bocaabajo', imagen: './Imagenes/back.png' };
    
    const nuevasCartas = [...cartas];
    nuevasCartas[0] = nuevaCarta;

    setCartas(nuevasCartas);
    };

    return (
    <div className="crupier">
        <Mano cartas={cartas} titulo="Crupier" />
    <div className="botonCrupier">
            <button className="boton4" onClick={mostrarPrimeraCarta ? solicitarCarta : cambiarCartaBocaAbajo}>
                {mostrarPrimeraCarta ? 'Solicitar Carta' : 'Cambiar Carta'}
            </button>
        </div>
    </div>
    );
};

export default Crupier;
