import React, { useState } from 'react';
import Mano from '../Mano';

const Crupier = ({ cartas, solicitarCarta }) => {
    const [mostrarPrimeraCarta, setMostrarPrimeraCarta] = useState(false);
    const [cartaAdicional, setCartaAdicional] = useState(null);

    const solicitarCartaCrupier = () => {
        solicitarCarta();

        // Después de solicitar una carta, la primera carta se mostrará boca arriba
        setMostrarPrimeraCarta(true);
    };

    const cambiarCartaBocaAbajo = () => {
        // Cambiar la primera carta por una nueva boca arriba
        const nuevaCarta = { numero: '0', palo: 'bocaabajo' };
        setCartaAdicional(nuevaCarta);
    };

    return (
        <div className="crupier">
        <Mano cartas={mostrarPrimeraCarta ? [...cartas, cartaAdicional] : cartas} titulo="Crupier" />
        <div className="botonCrupier">
            <button className="boton4" onClick={mostrarPrimeraCarta ? solicitarCartaCrupier : cambiarCartaBocaAbajo}>
            {mostrarPrimeraCarta ? 'Solicitar Carta' : 'Cambiar Carta'}
            </button>
        </div>
        </div>
    );
};

export default Crupier;
