const Crupier = ({ cartas, mostrarPrimeraCarta, solicitarCarta }) => {
    return (
      <div className="crupier">
        <div className="cartas-container">
          {cartas.map((carta, index) => (
            <div key={index} style={{ marginLeft: `${index * 20}px` }}>
              {index === 0 ? (
                mostrarPrimeraCarta ? (
                  <Cartas numero={carta.numero} palo={carta.palo} />
                ) : (
                  <img src="./Imagenes/naipes/dorso.png" alt="Carta boca abajo" style={{ width: '110px' }} />
                )
              ) : null}
            </div>
          ))}
        </div>
        {!mostrarPrimeraCarta && (
          <div className="botonCrupier">
            <button className="boton4" onClick={solicitarCarta}>
              Solicitar Carta
            </button>
          </div>
        )}
      </div>
    );
  };

export default Crupier;
