import React, { useState } from 'react';
import Mano from '../Mano';

const Crupier = ({ cartas, solicitarCarta, mostrarPrimeraCarta }) => {
    const [primeraCartaBocaAbajo, setPrimeraCartaBocaAbajo] = useState(true);

    const mostrarCartaBocaAbajo = primeraCartaBocaAbajo && !mostrarPrimeraCarta;
    
    const plantarse = () => {
        setPrimeraCartaBocaAbajo(false); // Cambiar el estado para mostrar la primera carta boca arriba
        solicitarCarta();
    };

    return (
        <div className="crupier">
            <Mano cartas={cartas.map((carta, index) => {
                if (index === 0 && mostrarCartaBocaAbajo) { 
                    return { numero: '0', palo: 'bocaAbajo' };
                }
                return carta;
            })} titulo="Crupier" />
            <div className="botonCrupier">
                <button className="boton4" onClick={solicitarCarta}>
                    Solicitar Carta
                </button>
            </div>
        </div>
    );
};

export default Crupier;
