import React from 'react';

const Cartas = ({ numero, palo, index }) => {
    const imagen = `./Imagenes/${palo}/${numero}.png`;
    const leftPosition = index * 70; // Ajusta este valor según el ancho de tus cartas y el espacio que desees entre ellas

    return (
        <div className="carta" style={{ position: 'absolute', left: `${leftPosition}px`}}>
        <img src={imagen} alt={`Carta ${numero} de ${palo}`}  style={{ width: '10%', height: '10%' }} />
        </div>
    );
};

export default Cartas;
