import React from 'react';
import Cartas from '../Cartas';

const Mano = ({ cartas, titulo }) => {
    // Calcular la puntuación total de la mano
    const calcularPuntuacion = () => {
        let puntuacion = 0;
        let ases = 0;
        
        const calcularPuntuacion = () => {
            let puntuacion = 0;
            let ases = 0;

            for (const carta of cartas) {
                if (carta.numero === 'A') {
                    ases++;
                    puntuacion += 11;
                } else if (['K', 'Q', 'J'].includes(carta.numero)) {
                    puntuacion += 10;
                } else {
                    puntuacion += parseInt(carta.numero);
                }
            }
            while (puntuacion > 21 && ases) {
                puntuacion -= 10;
                ases--;
            }
            return puntuacion;
        };
        return (
            <div className="mano">
                <h2>{titulo}</h2>
                <div className="cartas-container">
                    {cartas.map((carta, index) => (
                        <Cartas key={index} numero={carta.numero} palo={carta.palo} />
                    ))}
                </div>
                <p>Puntuación: {calcularPuntuacion()}</p>
            </div>
        );
};
}

export default Mano;
