import React from 'react';
import Mano from '../Mano';

const Crupier = ({ cartas, mostrarPrimeraCarta, solicitarCarta }) => {
    return (
        <div className="crupier row justify-content-center">
        <Mano cartas={cartas} titulo="Crupier" className="col-12" />
            {!mostrarPrimeraCarta && (
                <button className="boton4 col-12" onClick={solicitarCarta}>
                    Solicitar Carta
                </button>
            )}
        </div>
    );
};

export default Crupier;
