import React from 'react';
import Cartas from '../Cartas';

const Mano = ({ cartas }) => {
    const calcularPuntuacion = (cartas) => {
        let puntuacion = 0;
        let ases = 0;
        let valorCartas = 0;
      
        for (const carta of cartas) {
          if (carta.isFaceDown) {
            // Si la carta está boca abajo, suma su valor a 'valorCartas' pero no a 'puntuacion'
            valorCartas += parseInt(carta.numero);
          } else {
            if (carta.numero === 'A') {
              ases++;
              puntuacion += 11;
            } else if (['K', 'Q', 'J'].includes(carta.numero)) {
              puntuacion += 10;
            } else {
              puntuacion += parseInt(carta.numero);
            }
          }
        }
      
        while (puntuacion > 21 && ases) {
          puntuacion -= 10;
          ases--;
        }
      
        return { puntuacion, valorCartas };
      };
        return (
            <div className="mano">
                <div className="cartas-container">
                    {cartas.map((carta, index) => (
                        <div key={index} style={{ marginLeft: `${index * 20}px` }}>
                            <Cartas 
                                numero={carta.numero} 
                                palo={carta.palo} 
                                isFaceDown={carta.isFaceDown} 
                            />
                        </div>
                    ))}
                </div>
                <p>Puntuación: {calcularPuntuacion()}</p>
            </div>
        );
    };

export default Mano;
